export const dataService = {
    async seedData() {
        try {
            logger.info('Seeding data...');
            
            // Seed Articles
            const articleCount = await db.count(DB_STORES.ARTICLES);
            if (articleCount === 0) {
                for (const item of seedArticles) {
                    // Map to DB structure
                    const article = {
                        slug: item.id,
                        title: item.title,
                        content: item.content,
                        coverImage: item.image,
                        date: new Date().toISOString(),
                        url: item.url,
                        categories: [item.category]
                    };
                    await db.put(DB_STORES.ARTICLES, article);
                }
                logger.info(`Seeded ${seedArticles.length} articles`);
            }

            // Seed Pages
            const pageCount = await db.count(DB_STORES.PAGES);
            if (pageCount === 0) {
                for (const item of seedPages) {
                     const page = {
                        slug: item.id,
                        title: item.title,
                        content: item.content,
                        coverImage: item.image,
                        url: item.url,
                        meta: { description: item.content.substring(0, 150) }
                    };
                    await db.put(DB_STORES.PAGES, page);
                }
                logger.info(`Seeded ${seedPages.length} pages`);
            }
            
            logger.info('Seeding complete');
        } catch (error) {
            logger.error('Error seeding data:', error);
        }
    },
    
    async clearDatabase() {
        try {
            await db.clear(DB_STORES.ARTICLES);
            await db.clear(DB_STORES.PAGES);
            logger.info('Database cleared');
        } catch (error) {
            logger.error('Error clearing database:', error);
        }
    }
};
